import React, { useState } from 'react';
import "./List.scss";
import Card from "../Card/Card.jsx";
import useFetch from '../../hooks/useFetch';

const List = ({ subCats, maxPrice, sort, categoryId }) => {

    // let query = `/posters?populate=*&[filters][poster_materials][id]=${categoryId}${subCats.map(
    //     (item) => `&[filters][sub_categories][id][$eq]=${item}`
    // )}&[filters][price][$lte]=${maxPrice}}`

    // if (sort !== null) {
    //     query += `&sort=price:${sort}`;
    // }

    // const { data, loading, error } = useFetch(
    //     query
    // );

    const { data, loading, error } = useFetch(
        `/posters?populate=*&[filters][poster_materials][id]=${categoryId}${subCats.map(
                (item) => `&[filters][sub_categories][id][$eq]=${item}`
            )}&[filters][price][$lte]=${maxPrice}}`
      );

    return (
        <div className="list">
            {error ? "Oops! Something went wrong." : 
            (loading ? "loading..." : 
            data?.map((item) => 
                <Card item={item} key={item.id}/>
            ))}
        </div>
    )
}

export default List;
